import{_ as a,o as e,c as h,d as r}from"./app.3a811a3c.js";const d={},i=r('<h1 id="\u6570\u636E\u4ED3\u5E93\u5EFA\u6A21" tabindex="-1"><a class="header-anchor" href="#\u6570\u636E\u4ED3\u5E93\u5EFA\u6A21" aria-hidden="true">#</a> \u6570\u636E\u4ED3\u5E93\u5EFA\u6A21</h1><h2 id="\u6982\u8FF0" tabindex="-1"><a class="header-anchor" href="#\u6982\u8FF0" aria-hidden="true">#</a> \u6982\u8FF0</h2><p>\u7EF4\u5EA6\u5EFA\u6A21\u662F\u4E00\u79CD\u5C06\u6570\u636E\u7ED3\u6784\u5316\u7684\u903B\u8F91\u8BBE\u8BA1\u65B9\u6CD5\uFF0C\u5B83\u5C06\u5BA2\u89C2\u4E16\u754C\u5212\u5206\u4E3A\u5EA6\u91CF\u548C\u4E0A\u4E0B\u6587\u3002\u5EA6\u91CF\u662F\u5E38\u5E38\u662F\u4EE5\u6570\u503C\u5F62\u5F0F\u51FA\u73B0\uFF0C \u4E8B\u5B9E\u5468\u56F4\u6709\u4E0A\u4E0B\u6587\u5305\u56F4\u7740\uFF0C\u8FD9\u79CD\u4E0A\u4E0B\u6587\u88AB\u76F4\u89C2\u5730\u5206\u6210\u72EC\u7ACB\u7684\u903B\u8F91\u5757\uFF0C\u79F0\u4E4B\u4E3A\u7EF4\u5EA6\u3002\u5B83\u4E0E\u5B9E\u4F53-\u5173\u7CFB\u5EFA\u6A21\u6709\u5F88\u5927\u7684\u533A\u522B\uFF0C \u5B9E\u4F53-\u5173\u7CFB\u5EFA\u6A21\u662F\u9762\u5411\u5E94\u7528\uFF0C\u9075\u5FAA\u7B2C\u4E09\u8303\u5F0F\uFF0C\u4EE5\u6D88\u9664\u6570\u636E\u5197\u4F59\u4E3A\u76EE\u6807\u7684\u8BBE\u8BA1\u6280\u672F\u3002\u7EF4\u5EA6\u5EFA\u6A21\u662F\u9762\u5411\u5206\u6790\uFF0C\u4E3A\u4E86\u63D0\u9AD8 \u67E5\u8BE2\u6027\u80FD\u53EF\u4EE5\u589E\u52A0\u6570\u636E\u5197\u4F59\uFF0C\u53CD\u89C4\u8303\u5316\u7684\u8BBE\u8BA1\u6280\u672F</p><h2 id="\u7EF4\u5EA6\u5EFA\u6A21" tabindex="-1"><a class="header-anchor" href="#\u7EF4\u5EA6\u5EFA\u6A21" aria-hidden="true">#</a> \u7EF4\u5EA6\u5EFA\u6A21</h2><h3 id="\u4E8B\u5B9E\u8868" tabindex="-1"><a class="header-anchor" href="#\u4E8B\u5B9E\u8868" aria-hidden="true">#</a> \u4E8B\u5B9E\u8868</h3><p>\u4E8B\u5B9E\u8868\u5B58\u50A8\u4E86\u4ECE\u4E1A\u52A1\u6D3B\u52A8\u6216\u4E8B\u4EF6\u63D0\u70BC\u51FA\u6765\u7684\u6027\u80FD\u5EA6\u91CF\uFF0C\u5B83\u4E3B\u8981\u5305\u542B\u7EF4\u5EA6\u8868\u7684\u5916\u952E\u548C\u8FDE\u7EED\u53D8\u5316\u7684\u53EF\u52A0\u6027\u6570\u503C\u6216\u534A\u53EF\u52A0\u4E8B\u5B9E\u3002 \u4E8B\u5B9E\u8868\u4EA7\u751F\u4E8E\u4E1A\u52A1\u8FC7\u7A0B\u4E2D\u800C\u4E0D\u662F\u4E1A\u52A1\u8FC7\u7A0B\u7684\u63CF\u8FF0\u6027\u4FE1\u606F\u3002\u5B83\u4E00\u822C\u662F\u884C\u591A\u5217\u5C11\uFF0C\u5360\u4E86\u6570\u636E\u4ED3\u5E93\u7684 90%\u7684\u7A7A\u95F4\u3002 \u5728\u7EF4\u5EA6\u6A21\u578B\u4E2D\u4E5F\u6709\u8868\u793A\u591A\u5BF9\u591A\u5173\u7CFB\u7684\u4E8B\u5B9E\uFF0C\u5176\u4ED6\u90FD\u662F\u7EF4\u5EA6\u8868\u3002</p><p>\u4E8B\u5B9E\u8868\u4F5C\u4E3A\u6570\u636E\u4ED3\u5E93\u7EF4\u5EA6\u5EFA\u6A21\u7684\u6838\u5FC3\uFF0C\u7D27\u7D27\u56F4\u7ED5\u7740\u4E1A\u52A1\u8FC7\u7A0B\u6765\u8BBE\u8BA1\uFF0C\u901A\u8FC7\u83B7\u53D6\u63CF\u8FF0\u4E1A\u52A1\u8FC7\u7A0B\u7684\u5EA6\u91CF\u6765\u8868\u8FBE\u4E1A\u52A1\u8FC7\u7A0B\uFF0C \u5305\u542B\u4E86\u5F15\u7528\u7684\u7EF4\u5EA6\u548C\u4E0E\u4E1A\u52A1\u8FC7\u7A0B\u6709\u5173\u7684\u5EA6\u91CF\u3002</p><p>\u4E8B\u5B9E\u8868\u4E2D\u4E00\u6761\u8BB0\u5F55\u6240\u8868\u8FBE\u7684\u4E1A\u52A1\u7EC6\u8282\u7A0B\u5EA6\u88AB\u79F0\u4E3A\u7C92\u5EA6\u3002\u901A\u5E38\u7C92\u5EA6\u53EF\u4EE5\u901A\u8FC7\u4E24\u79CD\u65B9\u5F0F\u6765\u8868\u8FF0\uFF1A\u4E00\u79CD\u662F\u7EF4\u5EA6\u5C5E\u6027\u7EC4\u5408\u6240 \u8868\u793A\u7684\u7EC6\u8282\u7A0B\u5EA6\uFF1B\u4E00\u79CD\u662F\u6240\u8868\u793A\u7684\u5177\u4F53\u4E1A\u52A1\u542B\u4E49\u3002</p><p>\u4F5C\u4E3A\u5EA6\u91CF\u4E1A\u52A1\u8FC7\u7A0B\u7684\u4E8B\u5B9E\uFF0C\u4E00\u822C\u4E3A\u6574\u578B\u6216\u6D6E\u70B9\u578B\u7684\u5341\u8FDB\u5236\u6570\u503C\uFF0C\u6709\u53EF\u52A0\u6027\u3001\u534A\u53EF\u52A0\u6027\u548C\u4E0D\u53EF\u52A0\u6027\u4E09\u79CD\u7C7B\u578B\u3002\u53EF\u52A0\u6027\u4E8B\u5B9E \u662F\u6307\u53EF\u4EE5\u6309\u7167\u4E0E\u4E8B\u5B9E\u8868\u5173\u8054\u7684\u4EFB\u610F\u7EF4\u5EA6\u8FDB\u884C\u6C47\u603B\u3002\u534A\u53EF\u52A0\u6027\u4E8B\u5B9E\u53EA\u80FD\u6309\u7167\u7279\u5B9A\u7EF4\u5EA6\u6C47\u603B\uFF0C\u4E0D\u80FD\u5BF9\u6240\u6709\u7EF4\u5EA6\u6C47\u603B\uFF0C\u6BD4\u5982\u5E93 \u5B58\u53EF\u4EE5\u6309\u7167\u5730\u70B9\u548C\u5546\u54C1\u8FDB\u884C\u6C47\u603B\uFF0C\u800C\u6309\u65F6\u95F4\u7EF4\u5EA6\u628A\u4E00\u5E74\u4E2D\u6BCF\u4E2A\u6708\u7684\u5E93\u5B58\u7D2F\u52A0\u8D77\u6765\u5219\u6BEB\u65E0\u610F\u4E49\u3002 \u8FD8\u6709\u4E00\u79CD\u5EA6\u91CF\u5B8C\u5168\u4E0D\u5177\u5907\u53EF\u52A0\u6027\uFF0C\u6BD4\u5982\u6BD4\u7387\u578B\u4E8B\u5B9E\u3002\u5BF9\u4E8E\u4E0D\u53EF\u52A0\u6027\u4E8B\u5B9E\u53EF\u5206\u89E3\u4E3A\u53EF\u52A0\u7684\u7EC4\u4EF6\u6765\u5B9E\u73B0\u805A\u96C6\u3002</p><p>\u4E8B\u5B9E\u8868\u901A\u5E38\u53EA\u6709\u5F88\u5C11\u7684\u5217\u548C\u5F88\u591A\u884C\uFF0C\u662F\u4E00\u79CD\u201C\u7626\u9AD8\u201D\u578B\u7684\u8868\u3002\u4E8B\u5B9E\u8868\u5B9A\u4E49\u4E3A\u4EE5\u4E0B\u4E09\u79CD\u7C7B\u578B\u4E4B\u4E00\uFF1A</p><p>\u4E8B\u52A1\u4E8B\u5B9E\u8868:\u8BB0\u5F55\u6709\u5173\u7279\u5B9A\u4E8B\u4EF6\u7684\u4E8B\u5B9E\uFF08\u4F8B\u5982\uFF0C\u9500\u552E\u4E8B\u4EF6\uFF0C\u4FDD\u5B58\u5728\u539F\u5B50\u7684\u7C92\u5EA6\uFF0C\u4E5F\u79F0\u4E3A\u539F\u5B50\u4E8B\u5B9E\u8868\uFF09 \u5468\u671F\u5FEB\u7167\u4E8B\u5B9E\u8868\u8BB0\u5F55\u7ED9\u5B9A\u65F6\u95F4\u70B9\u7684\u4E8B\u5B9E\uFF08\u4F8B\u5982\uFF0C\u6708\u672B\u7684\u5E10\u6237\u8BE6\u7EC6\u4FE1\u606F\uFF09 \u7D2F\u79EF\u5FEB\u7167\u4E8B\u5B9E\u8868\u8BB0\u5F55\u4E86\u7ED9\u5B9A\u65F6\u95F4\u70B9\u7684\u6C47\u603B\u4E8B\u5B9E\uFF08\u4F8B\u5982\uFF0C\u67D0\u4EA7\u54C1\u7684\u5F53\u6708\u8FC4\u4ECA\u603B\u9500\u552E\u989D\uFF09</p><h3 id="\u7EF4\u8868" tabindex="-1"><a class="header-anchor" href="#\u7EF4\u8868" aria-hidden="true">#</a> \u7EF4\u8868</h3><p>\u7EF4\u5EA6\u662F\u7EF4\u5EA6\u5EFA\u6A21\u7684\u57FA\u7840\u548C\u7075\u9B42\u3002\u5728\u7EF4\u5EA6\u5EFA\u6A21\u4E2D\uFF0C\u5C06\u5EA6\u91CF\u79F0\u4E3A\u4E8B\u5B9E\uFF0C\u5C06\u73AF\u5883\u63CF\u8FF0\u4E3A\u7EF4\u5EA6\uFF0C\u7EF4\u5EA6\u662F\u7528\u4E8E\u5206\u6790\u4E8B\u5B9E\u6240\u9700\u8981\u7684\u591A\u6837\u73AF\u5883\u3002 \u4F8B\u5982\uFF0C\u5728\u5206\u6790\u4EA4\u6613\u8FC7\u7A0B\u65F6\uFF0C\u53EF\u4EE5\u901A\u8FC7\u4E70\u5BB6\u3001\u5356\u5BB6\u3001\u5546\u54C1\u548C\u65F6\u95F4\u7B49\u7EF4\u5EA6\u63CF\u8FF0\u4EA4\u6613\u53D1\u751F\u7684\u73AF\u5883\u3002\u7EF4\u5EA6\u6240\u5305\u542B\u7684\u8868\u793A\u7EF4\u5EA6\u7684\u5217\uFF0C\u79F0\u4E3A \u7EF4\u5EA6\u5C5E\u6027\u3002\u7EF4\u5EA6\u5C5E\u6027\u662F\u67E5\u8BE2\u7EA6\u675F\u6761\u4EF6\u3001\u5206\u7EC4\u548C\u62A5\u8868\u6807\u7B7E\u751F\u6210\u7684\u57FA\u672C\u6765\u6E90\uFF0C\u662F\u6570\u636E\u6613\u7528\u6027\u7684\u5173\u952E\u3002</p><p>\u7EF4\u5EA6\u901A\u5E38\u662F\u9650\u5B9A\u4E8B\u5B9E\u7684\u63CF\u8FF0\u6027\u4FE1\u606F\u3002\u4F8B\u5982\uFF0C\u4EA7\u54C1\u7EF4\u5EA6\u4E2D\u7684\u6BCF\u4E2A\u8BB0\u5F55\u4EE3\u8868\u4E00\u4E2A\u7279\u5B9A\u7684\u4EA7\u54C1\u3002\u4E0E\u4E8B\u5B9E\u8868\u76F8\u6BD4\uFF0C\u7EF4\u8868\u901A\u5E38\u5177\u6709\u76F8\u5BF9\u8F83\u5C11 \u7684\u8BB0\u5F55\uFF0C\u4F46\u662F\u6BCF\u4E2A\u8BB0\u5F55\u53EF\u80FD\u5177\u6709\u5927\u91CF\u7684\u5C5E\u6027\u6765\u63CF\u8FF0\u4E8B\u5B9E\u6570\u636E\u3002\u7EF4\u5EA6\u53EF\u4EE5\u5B9A\u4E49\u5404\u79CD\u5404\u6837\u7684\u7279\u5F81\uFF0C\u4E00\u4E9B\u5E38\u89C1\u7684\u7EF4\u8868\uFF1A</p><ul><li>\u65F6\u95F4\u7EF4\u5EA6\u8868\uFF1A\u4EE5\u6700\u4F4E\u65F6\u95F4\u7C92\u5EA6\u7EA7\u522B\u63CF\u8FF0\u65F6\u95F4</li><li>\u5730\u7406\u7EF4\u5EA6\u8868\uFF1A\u63CF\u8FF0\u4E86\u4F4D\u7F6E\u6570\u636E\uFF0C\u4F8B\u5982\u56FD\u5BB6/\u5730\u533A/\u57CE\u5E02</li><li>\u4EA7\u54C1\u7EF4\u5EA6\u8868\uFF1A\u8868\u63CF\u8FF0\u4E86\u4EA7\u54C1\u7684\u8BE6\u7EC6\u4FE1\u606F</li><li>\u6210\u5458\u7EF4\u5EA6\u8868\uFF1A\u63CF\u8FF0\u4E86\u6210\u5458\uFF0C\u4F8B\u5982\u9500\u552E\u4EBA\u5458</li></ul><h2 id="\u7EF4\u5EA6\u5EFA\u6A21\u7684\u4F18\u7F3A\u70B9" tabindex="-1"><a class="header-anchor" href="#\u7EF4\u5EA6\u5EFA\u6A21\u7684\u4F18\u7F3A\u70B9" aria-hidden="true">#</a> \u7EF4\u5EA6\u5EFA\u6A21\u7684\u4F18\u7F3A\u70B9</h2><h3 id="\u4F18\u70B9" tabindex="-1"><a class="header-anchor" href="#\u4F18\u70B9" aria-hidden="true">#</a> \u4F18\u70B9</h3><p>\u6BCF\u6B21\u9700\u8981\u4ECE\u6570\u636E\u5E93\u4E2D\u83B7\u53D6\u4E00\u4E9B\u4FE1\u606F\u65F6\uFF0C\u53EF\u4EE5\u4E0D\u7528\u7F16\u5199\u5197\u957F\u7684\u67E5\u8BE2 \u9488\u5BF9\u8BFB\u53D6\u8FDB\u884C\u4E86\u4F18\u5316\uFF0C\u53EF\u4EE5\u5199\u66F4\u5C11\u7684 JOIN\uFF0C\u66F4\u5FEB\u5730\u8FD4\u56DE\u7ED3\u679C</p><h3 id="\u7F3A\u70B9" tabindex="-1"><a class="header-anchor" href="#\u7F3A\u70B9" aria-hidden="true">#</a> \u7F3A\u70B9</h3><p>\u5BF9\u6570\u636E\u8FDB\u884C\u975E\u89C4\u8303\u5316\u610F\u5473\u7740\u4E00\u6B21\u6027\u63D2\u5165\u6216\u66F4\u65B0\u4F1A\u5BFC\u81F4\u6570\u636E\u5F02\u5E38\u3002\u5728\u5B9E\u8DF5\u4E2D\uFF0C\u661F\u578B\u6A21\u578B\u662F\u901A\u8FC7\u6279\u5904\u7406\u5B9E\u6765\u5F25\u8865\u8FD9\u4E00\u95EE\u9898 \u5206\u6790\u7075\u6D3B\u6027\u6709\u9650\u3002\u661F\u578B\u6A21\u578B\u901A\u5E38\u662F\u4E3A\u7279\u5B9A\u76EE\u7684\u800C\u8BBE\u8BA1\u7684\u3002\u5728\u5206\u6790\u9700\u6C42\u65B9\u9762\uFF0C\u5B83\u4E0D\u50CF\u89C4\u8303\u5316\u6570\u636E\u6A21\u578B\u90A3\u6837\u7075\u6D3B</p><h2 id="\u7EF4\u5EA6\u8BBE\u8BA1\u7684\u4E3B\u8981\u6D41\u7A0B" tabindex="-1"><a class="header-anchor" href="#\u7EF4\u5EA6\u8BBE\u8BA1\u7684\u4E3B\u8981\u6D41\u7A0B" aria-hidden="true">#</a> \u7EF4\u5EA6\u8BBE\u8BA1\u7684\u4E3B\u8981\u6D41\u7A0B</h2><h3 id="\u9009\u62E9\u4E1A\u52A1\u8FC7\u7A0B" tabindex="-1"><a class="header-anchor" href="#\u9009\u62E9\u4E1A\u52A1\u8FC7\u7A0B" aria-hidden="true">#</a> \u9009\u62E9\u4E1A\u52A1\u8FC7\u7A0B</h3><p>\u4E1A\u52A1\u8FC7\u7A0B\u662F\u7EC4\u7EC7\u5B8C\u6210\u7684\u64CD\u4F5C\u6027\u6D3B\u52A8\uFF0C\u4F8B\u5982\uFF1A\u83B7\u5F97\u8BA2\u5355\u3001\u5904\u7406\u4FDD\u9669\u7D22\u8D54\u3001\u5B66\u751F\u8BFE\u7A0B\u6CE8\u518C\u6216\u6BCF\u4E2A\u6708\u6BCF\u4E2A\u8D26\u5355\u7684\u5FEB\u7167\u7B49\u3002 \u4E1A\u52A1\u8FC7\u7A0B\u4E8B\u4EF6\u5EFA\u7ACB\u6216\u83B7\u53D6\u6027\u80FD\u5EA6\u91CF\uFF0C\u5E76\u8F6C\u6362\u6210\u4E8B\u5B9E\u8868\u4E2D\u7684\u4E8B\u5B9E\u3002\u8FC7\u7A0B\u5B9A\u4E49\u4E86\u7279\u5B9A\u7684\u8BBE\u8BA1\u76EE\u6807\u4EE5\u53CA\u5BF9\u7C92\u5EA6\u3001\u7EF4\u5EA6\u3001\u4E8B\u5B9E\u7684\u5B9A\u4E49\u3002</p><h3 id="\u58F0\u660E\u7C92\u5EA6" tabindex="-1"><a class="header-anchor" href="#\u58F0\u660E\u7C92\u5EA6" aria-hidden="true">#</a> \u58F0\u660E\u7C92\u5EA6</h3><p>\u7C92\u5EA6\u7528\u4E8E\u786E\u5B9A\u67D0\u4E00\u4E2A\u4E8B\u5B9E\u8868\u4E2D\u7684\u884C\u8868\u793A\u4EC0\u4E48\u3002\u7C92\u5EA6\u58F0\u660E\u662F\u8BBE\u8BA1\u5FC5\u987B\u5C65\u884C\u7684\u5408\u540C\u3002\u5728\u9009\u62E9\u7EF4\u5EA6\u6216\u4E8B\u5B9E\u524D\u5FC5\u987B\u58F0\u660E\u7C92\u5EA6\uFF0C \u67D0\u4E2A\u5019\u9009\u7EF4\u5EA6\u6216\u4E8B\u5B9E\u5FC5\u987B\u4E0E\u5B9A\u4E49\u7684\u7C92\u5EA6\u4FDD\u6301\u4E00\u81F4\u3002\u5728\u6240\u6709\u7EF4\u5EA6\u8BBE\u8BA1\u4E2D\u5F3A\u5236\u5B9E\u884C\u4E00\u81F4\u6027\u662F\u4FDD\u8BC1 BI \u5E94\u7528\u6027\u80FD\u548C\u6613\u7528\u6027\u7684\u5173\u952E\u3002</p><p>\u539F\u5B50\u7C92\u5EA6\u662F\u6700\u4F4E\u7EA7\u522B\u7684\u7C92\u5EA6\u3002\u5EFA\u8BAE\u4ECE\u5173\u6CE8\u539F\u5B50\u7EA7\u522B\u7C92\u5EA6\u6570\u636E\u5F00\u59CB\u8BBE\u8BA1\u3002\u540C\u65F6\uFF0C\u4E0A\u5377\u6C47\u603B\u7C92\u5EA6\u5BF9\u6027\u80FD\u8C03\u6574\u6765\u8BF4\u975E\u5E38\u91CD\u8981\u3002 \u9488\u5BF9\u4E0D\u540C\u7684\u4E8B\u5B9E\u8868\u7C92\u5EA6\uFF0C\u8981\u5EFA\u7ACB\u4E0D\u540C\u7684\u7269\u7406\u8868\uFF0C\u5728\u540C\u4E00\u4E8B\u5B9E\u8868\u4E2D\u4E0D\u8981\u6DF7\u7528\u591A\u79CD\u4E0D\u540C\u7684\u7C92\u5EA6\u3002</p><h3 id="\u786E\u8BA4\u7EF4\u5EA6" tabindex="-1"><a class="header-anchor" href="#\u786E\u8BA4\u7EF4\u5EA6" aria-hidden="true">#</a> \u786E\u8BA4\u7EF4\u5EA6</h3><p>\u7EF4\u5EA6\u63D0\u4F9B\u56F4\u7ED5\u67D0\u4E00\u4E1A\u52A1\u8FC7\u7A0B\u4E8B\u4EF6\u6240\u6D89\u53CA\u7684\u201C\u8C01\u3001\u4EC0\u4E48\u3001\u4F55\u5904\u3001\u4F55\u65F6\u3001\u4E3A\u4EC0\u4E48\u3001\u5982\u4F55\u201D\u7B49\u80CC\u666F\u3002\u7EF4\u5EA6\u8868\u5305\u542B BI \u5E94\u7528\u6240\u9700\u8981\u7684 \u7528\u4E8E\u8FC7\u6EE4\u53CA\u5206\u7C7B\u4E8B\u5B9E\u7684\u63CF\u8FF0\u6027\u5C5E\u6027\uFF08\u8FD9\u4E5F\u662F\u8BC6\u522B\u7EF4\u5EA6\u7684\u91CD\u8981\u4F9D\u636E\uFF09\u3002\u5F53\u4E0E\u8DDF\u5B9A\u7684\u4E8B\u5B9E\u8868\u884C\u5173\u8054\u65F6\uFF0C\u4EFB\u4F55\u60C5\u51B5\u4E0B\u90FD\u5E94\u4F7F\u7EF4\u5EA6\u4FDD\u6301\u5355\u4E00\u503C\u3002</p><p>\u6570\u636E\u7BA1\u7406\u4E0E\u7EF4\u5EA6\u8868\u7684\u5F00\u653E\u662F\u6570\u636E\u4ED3\u5E93\u5EFA\u8BBE\u7684\u4E3B\u8981\u5DE5\u4F5C\u3002</p><h3 id="\u786E\u8BA4\u4E8B\u5B9E" tabindex="-1"><a class="header-anchor" href="#\u786E\u8BA4\u4E8B\u5B9E" aria-hidden="true">#</a> \u786E\u8BA4\u4E8B\u5B9E</h3><p>\u4E8B\u5B9E\u6D89\u53CA\u6765\u81EA\u4E1A\u52A1\u8FC7\u7A0B\u4E8B\u4EF6\u7684\u5EA6\u91CF\uFF0C\u57FA\u672C\u4E0A\u90FD\u662F\u4EE5\u6570\u91CF\u503C\u8868\u793A\u3002\u4E00\u4E2A\u4E8B\u5B9E\u8868\u884C\u4E0E\u6309\u7167\u4E8B\u5B9E\u8868\u7C92\u5EA6\u63CF\u8FF0\u7684\u5EA6\u91CF\u65F6\u95F4\u4E4B\u95F4\u5B58\u5728 \u4E00\u5BF9\u4E00\u7684\u5173\u7CFB\uFF0C\u4E8B\u5B9E\u8868\u5BF9\u5E94\u7684\u662F\u4E00\u4E2A\u7269\u7406\u53EF\u89C2\u5BDF\u7684\u4E8B\u4EF6\u3002\u6240\u6709\u4E8B\u5B9E\u53EA\u5141\u8BB8\u4E0E\u58F0\u660E\u7684\u7C92\u5EA6\u4FDD\u6301\u4E00\u81F4\u3002</p><h2 id="\u4E8B\u5B9E\u8868\u6280\u672F\u57FA\u7840" tabindex="-1"><a class="header-anchor" href="#\u4E8B\u5B9E\u8868\u6280\u672F\u57FA\u7840" aria-hidden="true">#</a> \u4E8B\u5B9E\u8868\u6280\u672F\u57FA\u7840</h2><h3 id="\u4E8B\u5B9E\u8868\u7ED3\u6784" tabindex="-1"><a class="header-anchor" href="#\u4E8B\u5B9E\u8868\u7ED3\u6784" aria-hidden="true">#</a> \u4E8B\u5B9E\u8868\u7ED3\u6784</h3><p>\u4E8B\u5B9E\u8868\u4E2D\u5B58\u50A8\u7684\u662F\u73B0\u5B9E\u4E16\u754C\u4E2D\u6240\u53D1\u751F\u7684\u64CD\u4F5C\u6027\u4E8B\u4EF6\u6240\u4EA7\u751F\u7684\u53EF\u5EA6\u91CF\u6570\u503C\u3002\u4ECE\u6700\u4F4E\u7EA7\u7684\u539F\u5B50\u7C92\u5EA6\u6765\u770B\uFF0C\u4E8B\u5B9E\u8868\u4E2D\u4E00\u884C\u8BB0\u5F55\u5BF9\u5E94 \u73B0\u5B9E\u4E16\u754C\u4E2D\u7684\u4E00\u4E2A\u5EA6\u91CF\u4E8B\u4EF6\u3002</p><p>\u4E8B\u5B9E\u8868\u7684\u8BBE\u8BA1\u5B8C\u5168\u4F9D\u8D56\u4E8E\u7269\u7406\u6D3B\u52A8\uFF0C\u4E0D\u53D7\u53EF\u80FD\u4EA7\u751F\u7684\u6700\u7EC8\u62A5\u8868\u7684\u5F71\u54CD\u3002\u4E8B\u5B9E\u8868\u603B\u662F\u5305\u542B\u5916\u952E\uFF0C\u7528\u4E8E\u5173\u8054\u4E0E\u4E4B\u76F8\u5173\u7684\u7EF4\u5EA6\uFF0C \u4E5F\u5305\u542B\u53EF\u9009\u7684\u9000\u5316\u7EF4\u5EA6\u5EFA\u548C\u65E5\u671F/\u65F6\u95F4\u6233\u3002\u67E5\u8BE2\u8BF7\u6C42\u7684\u4E3B\u8981\u76EE\u6807\u662F\u57FA\u4E8E\u4E8B\u5B9E\u8868\u5F00\u5C55\u8BA1\u7B97\u548C\u805A\u96C6\u64CD\u4F5C\u3002</p><h3 id="\u53EF\u52A0\u3001\u534A\u53EF\u52A0\u3001\u4E0D\u53EF\u52A0\u4E8B\u5B9E" tabindex="-1"><a class="header-anchor" href="#\u53EF\u52A0\u3001\u534A\u53EF\u52A0\u3001\u4E0D\u53EF\u52A0\u4E8B\u5B9E" aria-hidden="true">#</a> \u53EF\u52A0\u3001\u534A\u53EF\u52A0\u3001\u4E0D\u53EF\u52A0\u4E8B\u5B9E</h3><p>\u4E8B\u5B9E\u8868\u4E2D\u7684\u6570\u5B57\u5EA6\u91CF\u53EF\u5212\u5206\u4E3A\u4E09\u7C7B\uFF1A</p><h3 id="\u53EF\u52A0\u4E8B\u5B9E" tabindex="-1"><a class="header-anchor" href="#\u53EF\u52A0\u4E8B\u5B9E" aria-hidden="true">#</a> \u53EF\u52A0\u4E8B\u5B9E</h3><p>\u6700\u7075\u6D3B\u3001\u6700\u6709\u7528\u7684\u4E8B\u5B9E\u662F\u5B8C\u5168\u53EF\u52A0\uFF0C\u53EF\u52A0\u6027\u5EA6\u91CF\u53EF\u4EE5\u6309\u7167\u4E0E\u4E8B\u5B9E\u8868\u5173\u8054\u7684\u4EFB\u610F\u7EF4\u5EA6\u6C47\u603B\u3002</p><h4 id="\u534A\u53EF\u52A0\u4E8B\u5B9E" tabindex="-1"><a class="header-anchor" href="#\u534A\u53EF\u52A0\u4E8B\u5B9E" aria-hidden="true">#</a> \u534A\u53EF\u52A0\u4E8B\u5B9E</h4><p>\u534A\u53EF\u52A0\u5EA6\u91CF\u53EF\u4EE5\u5BF9\u67D0\u4E9B\u7EF4\u5EA6\u6C47\u603B\uFF0C\u4F46\u4E0D\u80FD\u5BF9\u6240\u6709\u7EF4\u5EA6\u6C47\u603B\u3002\u5DEE\u989D\u662F\u5E38\u89C1\u7684\u534A\u53EF\u52A0\u4E8B\u5B9E\u3002</p><h4 id="\u4E0D\u53EF\u52A0\u4E8B\u5B9E" tabindex="-1"><a class="header-anchor" href="#\u4E0D\u53EF\u52A0\u4E8B\u5B9E" aria-hidden="true">#</a> \u4E0D\u53EF\u52A0\u4E8B\u5B9E</h4><p>\u4E0D\u53EF\u52A0\u4E8B\u5B9E\u5BF9\u6240\u6709\u7684\u7EF4\u5EA6\u4E0D\u53EF\u52A0\uFF0C\u4F8B\u5982\u6BD4\u7387\u3002</p><h4 id="\u4E8B\u5B9E\u8868\u4E2D\u7684\u7A7A\u503C" tabindex="-1"><a class="header-anchor" href="#\u4E8B\u5B9E\u8868\u4E2D\u7684\u7A7A\u503C" aria-hidden="true">#</a> \u4E8B\u5B9E\u8868\u4E2D\u7684\u7A7A\u503C</h4><p>\u4E8B\u5B9E\u8868\u4E2D\u7684\u5EA6\u91CF\u503C\u53EF\u4EE5\u5B58\u5728\u7A7A\u503C\uFF0C\u56E0\u4E3A\u6240\u6709\u7684\u805A\u96C6\u51FD\u6570\uFF08SUM\u3001COUNT\u3001MIN\u3001MAX\u3001AVG\uFF09\u5747\u53EF\u4EE5\u5BF9\u7A7A\u503C\u8FDB\u884C\u8BA1\u7B97\u3002</p><p>\u4E8B\u5B9E\u8868\u7684\u7EF4\u5EA6\u5916\u952E\u4E0D\u80FD\u5B58\u5728\u7A7A\u503C\uFF0C\u5426\u5219\u4F1A\u5BFC\u81F4\u8FDD\u53CD\u53C2\u7167\u5B8C\u6574\u6027\u7684\u60C5\u51B5\u53D1\u751F\u3002\u5173\u8054\u7684\u7EF4\u5EA6\u8868\u5FC5\u987B\u7528\u9ED8\u8BA4\u884C\uFF08\u4EE3\u7406\u952E\uFF09\u800C\u4E0D\u662F\u7A7A\u503C\u5916\u952E\u8868\u793A\u672A\u77E5\u7684\u6216\u65E0\u6CD5\u5E94\u7528\u7684\u60C5\u51B5\u3002</p><h4 id="\u4E8B\u5B9E\u8868\u7684\u4E09\u79CD\u6807\u51C6\u7C7B\u578B" tabindex="-1"><a class="header-anchor" href="#\u4E8B\u5B9E\u8868\u7684\u4E09\u79CD\u6807\u51C6\u7C7B\u578B" aria-hidden="true">#</a> \u4E8B\u5B9E\u8868\u7684\u4E09\u79CD\u6807\u51C6\u7C7B\u578B</h4><p>\u4E8B\u52A1\u4E8B\u5B9E\u8868</p><p>\u4E8B\u52A1\u4E8B\u5B9E\u8868\u7684\u4E00\u884C\u5BF9\u5E94\u7A7A\u95F4\u6216\u65F6\u95F4\u4E0A\u67D0\u70B9\u7684\u5EA6\u91CF\u4E8B\u4EF6\u3002\u539F\u5B50\u4E8B\u52A1\u7C92\u5EA6\u4E8B\u5B9E\u8868\u662F\u7EF4\u5EA6\u5316\u53CA\u53EF\u8868\u8FBE\u7684\u4E8B\u5B9E\u8868\uFF0C\u8FD9\u7C7B\u5065\u58EE\u7684\u7EF4\u5EA6\u786E\u4FDD\u5BF9\u4E8B\u52A1\u6570\u636E\u7684\u6700\u5927\u5316\u5206\u7247\u548C\u5206\u5757\u3002</p><p>\u5468\u671F\u5FEB\u7167\u4E8B\u5B9E\u8868</p><p>\u5468\u671F\u5FEB\u7167\u4E8B\u5B9E\u8868\u4E2D\u7684\u6BCF\u884C\u6C47\u603B\u4E86\u53D1\u751F\u5728\u67D0\u4E00\u4E2A\u6807\u51C6\u5468\u671F\uFF0C\u5982\u67D0\u4E00\u5929\u3001\u67D0\u5468\u3001\u67D0\u6708\u7684\u591A\u4E2A\u5EA6\u91CF\u4E8B\u4EF6\u3002\u7C92\u5EA6\u662F\u5468\u671F\u6027\u7684\uFF0C\u800C\u4E0D\u662F\u4E2A\u4F53\u4E8B\u52A1\u3002\u5373\u4F7F\u5468\u671F\u5185\u6CA1\u6709\u6D3B\u52A8\u53D1\u751F\uFF0C\u4E5F\u4F1A\u5728\u4E8B\u5B9E\u8868\u4E2D\u4E3A\u6BCF\u4E2A\u4E8B\u5B9E\u63D2\u5165\u5305\u542B 0 \u6216\u7A7A\u503C\u7684\u884C\u3002\u5468\u671F\u5FEB\u7167\u4E8B\u5B9E\u8868\u7684\u4E00\u4E2A\u5178\u578B\u7684\u5E94\u7528\u573A\u666F\u662F\u5E93\u5B58\u3002</p><p>\u7D2F\u79EF\u5FEB\u7167\u4E8B\u5B9E\u8868</p><p>\u7D2F\u79EF\u5FEB\u7167\u4E8B\u5B9E\u8868\u7684\u884C\u6C47\u603B\u4E86\u53D1\u751F\u5728\u8FC7\u7A0B\u5F00\u59CB\u548C\u7ED3\u675F\u4E4B\u95F4\u53EF\u9884\u6D4B\u6B65\u9AA4\u5185\u7684\u5EA6\u91CF\u4E8B\u4EF6\u3002\u7BA1\u9053\u6216\u5DE5\u4F5C\u6D41\u8FC7\u7A0B\uFF08\u4F8B\u5982\uFF0C\u5C65\u884C\u8BA2\u5355\u6216\u7D22\u8D54\u8FC7\u7A0B\uFF09\u5177\u6709\u5B9A\u4E49\u7684\u5F00\u59CB\u70B9\uFF0C\u6807\u51C6\u4E2D\u95F4\u8FC7\u7A0B\uFF0C\u5B9A\u4E49\u7684\u7ED3\u675F\u70B9\uFF0C\u5B83\u4EEC\u5728\u6B64\u7C7B\u4E8B\u5B9E\u8868\u4E2D\u90FD\u53EF\u4EE5\u88AB\u5EFA\u6A21\u3002\u901A\u5E38\u5728\u4E8B\u5B9E\u8868\u4E2D\u9488\u5BF9\u8FC7\u7A0B\u4E2D\u7684\u5173\u952E\u6B65\u9AA4\u90FD\u5305\u542B\u65F6\u95F4\u5916\u952E\u3002\u7D2F\u79EF\u5FEB\u7167\u4E8B\u5B9E\u8868\u4E2D\u7684\u4E00\u884C\uFF0C\u5BF9\u5E94\u67D0\u4E00\u5177\u4F53\u7684\u8BA2\u5355\uFF0C\u5F53\u8BA2\u5355\u4EA7\u751F\u65F6\u4F1A\u63D2\u5165\u4E00\u884C\u3002\u4F46\u7BA1\u9053\u8FC7\u7A0B\u53D1\u751F\u65F6\uFF0C\u7D2F\u79EF\u4E8B\u5B9E\u8868\u884C\u88AB\u8BBF\u95EE\u5E76\u4FEE\u6539\uFF08\u8FD9\u662F\u5F88\u7279\u522B\u7684\uFF09\u3002</p><h3 id="\u7EF4\u5EA6\u8868\u6280\u672F\u57FA\u7840" tabindex="-1"><a class="header-anchor" href="#\u7EF4\u5EA6\u8868\u6280\u672F\u57FA\u7840" aria-hidden="true">#</a> \u7EF4\u5EA6\u8868\u6280\u672F\u57FA\u7840</h3><h4 id="\u7EF4\u5EA6\u8868\u7ED3\u6784" tabindex="-1"><a class="header-anchor" href="#\u7EF4\u5EA6\u8868\u7ED3\u6784" aria-hidden="true">#</a> \u7EF4\u5EA6\u8868\u7ED3\u6784</h4><p>\u6BCF\u4E2A\u7EF4\u5EA6\u8868\u90FD\u5305\u542B\u5355\u4E00\u7684\u4E3B\u952E\u5217\u3002\u7EF4\u5EA6\u8868\u7684\u4E3B\u952E\u53EF\u4EE5\u4F5C\u4E3A\u4E0E\u4E4B\u5173\u8054\u7684\u4EFB\u4F55\u4E8B\u5B9E\u8868\u7684\u5916\u952E\uFF0C\u5F53\u7136\uFF0C\u7EF4\u5EA6\u8868\u884C\u7684\u6280\u672F\u5B9A\u4E49\u5E94\u4E0E\u4E8B\u5B9E\u8868\u884C\u5B8C\u5168\u5BF9\u5E94\u3002</p><p>\u7EF4\u5EA6\u8868\u901A\u5E38\u6BD4\u8F83\u5BBD\uFF0C\u662F\u6241\u5E73\u578B\u975E\u89C4\u8303\u8868\uFF0C\u5305\u542B\u5927\u91CF\u7684\u4F4E\u7C92\u5EA6\u7684\u6587\u672C\u5C5E\u6027\u3002\u64CD\u4F5C\u4EE3\u7801\u4E0E\u6307\u793A\u5668\u53EF\u4F5C\u4E3A\u5C5E\u6027\u5BF9\u5F85\uFF0C\u6700\u5F3A\u6709\u529B\u7684\u7EF4\u5EA6\u5C5E\u6027\u91C7\u7528\u5197\u957F\u7684\u63CF\u8FF0\u586B\u5145\u3002</p><p>\u7EF4\u5EA6\u8868\u5C5E\u6027\u662F\u67E5\u8BE2\u53CA BI \u5E94\u7528\u7684\u7EA6\u675F\u548C\u5206\u7EC4\u5B9A\u4E49\u7684\u4E3B\u8981\u76EE\u6807\u3002\u62A5\u8868\u7684\u63CF\u8FF0\u6027\u6807\u8BC6\u901A\u5E38\u662F\u7EF4\u5EA6\u8868\u5C5E\u6027\u9886\u57DF\u503C\u3002</p><h4 id="\u7EF4\u5EA6\u4EE3\u7406\u952E" tabindex="-1"><a class="header-anchor" href="#\u7EF4\u5EA6\u4EE3\u7406\u952E" aria-hidden="true">#</a> \u7EF4\u5EA6\u4EE3\u7406\u952E</h4><p>\u7EF4\u5EA6\u8868\u4E2D\u4F1A\u5305\u542B\u4E00\u4E2A\u5217\uFF0C\u8868\u793A\u552F\u4E00\u4E3B\u952E\u3002\u8BE5\u4E3B\u952E\u4E0D\u662F\u64CD\u4F5C\u6027\u7CFB\u7EDF\u7684\u81EA\u7136\u95F4\u3002 \u7EF4\u5EA6\u4E3B\u952E\u901A\u5E38\u662F\u65E0\u610F\u4E49\u7684\u6574\u578B\u4E3B\u952E\uFF0C\u662F\u6309\u987A\u5E8F\u5206\u914D\u7684\u7B80\u5355\u6574\u6570\uFF0C\u4EE5\u503C 1 \u5F00\u59CB\uFF0C\u6BCF\u6B21\u5206\u914D\u81EA\u52A8\u52A0 1\u3002\u4F46\u65E5\u671F\u7EF4\u5EA6\u4E0D\u9700\u8981\u9075\u5B88\u4EE3\u7406\u952E\u89C4\u5219\u3002</p><h4 id="\u4E00\u81F4\u6027\u7EF4\u5EA6" tabindex="-1"><a class="header-anchor" href="#\u4E00\u81F4\u6027\u7EF4\u5EA6" aria-hidden="true">#</a> \u4E00\u81F4\u6027\u7EF4\u5EA6</h4><p>\u5F53\u4E0D\u540C\u7684\u7EF4\u5EA6\u8868\u5C5E\u6027\u5177\u6709\u76F8\u540C\u7684\u5217\u540D\u548C\u9886\u57DF\u5185\u5BB9\u65F6\uFF0C\u79F0\u7EF4\u5EA6\u8868\u5177\u6709\u4E00\u81F4\u6027\u3002</p><h4 id="\u7F13\u6162\u53D8\u5316\u7EF4\u5EA6" tabindex="-1"><a class="header-anchor" href="#\u7F13\u6162\u53D8\u5316\u7EF4\u5EA6" aria-hidden="true">#</a> \u7F13\u6162\u53D8\u5316\u7EF4\u5EA6</h4><p>\u7C7B\u578B\uFF1A</p><p>\u539F\u6837\u4FDD\u7559</p><p>\u91CD\u5199</p><p>\u589E\u52A0\u65B0\u884C</p><p>\u589E\u52A0\u65B0\u5C5E\u6027</p><p>\u589E\u52A0\u5FAE\u578B\u7EF4\u5EA6</p><p>\u589E\u52A0\u5FAE\u578B\u7EF4\u5EA6\u53CA\u7C7B\u578B 1 \u652F\u67B6</p><p>\u589E\u52A0\u7C7B\u578B 1 \u5C5E\u6027\u5230\u7C7B\u578B 2 \u7EF4\u5EA6</p><p>\u53CC\u7C7B\u578B 1 \u548C\u7C7B\u578B 2 \u7EF4\u5EA6</p>',72),p=[i];function n(t,c){return e(),h("div",null,p)}var l=a(d,[["render",n],["__file","02_\u7EF4\u5EA6\u5EFA\u6A21.html.vue"]]);export{l as default};
