import{_ as i,o as a,c as e,d as l}from"./app.53c39bda.js";var r="/solution/old_to_new_01.jpg",h="/solution/s2b.png";const d={},p=l('<h1 id="\u4F20\u7EDF\u4E1A\u52A1\u5347\u7EA7\u4E2D\u53F0\u67B6\u6784" tabindex="-1"><a class="header-anchor" href="#\u4F20\u7EDF\u4E1A\u52A1\u5347\u7EA7\u4E2D\u53F0\u67B6\u6784" aria-hidden="true">#</a> \u4F20\u7EDF\u4E1A\u52A1\u5347\u7EA7\u4E2D\u53F0\u67B6\u6784</h1><blockquote><p>\u4F20\u7EDF\u4E1A\u52A1\u5373\u4F20\u7EDF\u4E1A\u52A1\u7CFB\u7EDF\u548C\u56E2\u961F\u6A21\u5F0F</p></blockquote><h2 id="\u6982\u8FF0" tabindex="-1"><a class="header-anchor" href="#\u6982\u8FF0" aria-hidden="true">#</a> \u6982\u8FF0</h2><p>\u4ECE\u4F20\u7EDF\u4E1A\u52A1\u67B6\u6784\u7684\u670D\u52A1\u5668\u53D1\u5E03\uFF0C\u540C\u65F6\u4E5F\u5305\u62EC\u9879\u76EE\u7BA1\u7406\u5F00\u53D1\u6D41\u7A0B\uFF0C\u518D\u5230\u4F20\u7EDF\u7684\u9879\u76EE\u7BA1\u7406\u8FD0\u7EF4\u7B49\u8FDB\u884C\u67B6\u6784\u5347\u7EA7\uFF0C\u5347\u7EA7\u4E3A\u4E1A\u52A1\u4E2D\u53F0\u67B6\u6784\uFF0C\u4E3A\u4E0B\u4E00\u6B65\u6570\u636E\u8FD0\u8425\u7BA1\u7406\u505A\u597D\u57FA\u7840\u3002\u5347\u7EA7\u8FC7\u7A0B\u4ECE\u56E2\u961F\u3001\u4E13\u5BB6\u3001\u4E1A\u52A1\u3001\u67B6\u6784\u7B49\u51E0\u4E2A\u7EF4\u62A4\u8FDB\u884C\u7684\u8C03\u6574\uFF0C\u4EE5\u9002\u5E94\u5F53\u524DIT\u884C\u4E1A\u7684\u53D1\u5C55\u548C\u6570\u5B57\u5316\u5E02\u573A\u73AF\u5883\uFF0C\u5728\u8FD9\u4E2A\u5347\u7EA7\u7684\u8FC7\u7A0B\u4E0D\u4EC5\u4EC5\u662F\u6280\u672F\u9879\u76EE\u7684\u5347\u7EA7\uFF0C\u66F4\u591A\u7684\u662F\u7EC4\u7EC7\u67B6\u6784\uFF0C\u56E2\u961F\u6280\u80FD\u7684\u5347\u7EA7\uFF0C\u4EE5\u6D88\u9664\u524D\u671F\u4F20\u7EDF\u6A21\u5F0F\u7559\u4E0B\u7684\u5F0A\u7AEF\u3002</p><p>\u5347\u7EA7\u8FC7\u7A0B\u9610\u8FF0\u5206\u4E09\u4E2A\u7EF4\u62A4\uFF0C\u4E3B\u8981\u5347\u7EA7\u7684\u6210\u679C\u70B9\uFF1A</p><ul><li>\u4F20\u7EDF\u7CFB\u7EDF\u67B6\u6784\u5347\u7EA7\u4E2D\u53F0\u7CFB\u7EDF\u67B6\u6784\u6A21\u5F0F\uFF0C\u5373\u63D0\u53D6\u4E1A\u52A1\u4E2D\u53F0\uFF0C\u5927\u4E2D\u53F0\uFF0C\u5C0F\u524D\u53F0\u6A21\u5F0F\uFF1B</li><li>\u7BA1\u7406\u6A21\u5F0F\u8F6C\u6362\u6210\u4E2D\u53F0\u7EC4\u7EC7\u67B6\u6784\u6A21\u5F0F\uFF0C\u5373\u4E2D\u53F0\u4E13\u5BB6\u652F\u6301\uFF0C\u4E1A\u52A1\u5175\u56E2\u8D70\u524D\u7AEF\uFF1B</li><li>\u8F6F\u4EF6\u9879\u76EE\u5F00\u53D1\u8F6C\u6362\u6210\u884C\u4E1A\u4EA7\u54C1\u578B\u7814\u53D1\uFF0C\u5373\u8F93\u51FA\u9879\u76EE\u7684\u540C\u65F6\u8F93\u51FA\u4E2D\u53F0\u4EA7\u54C1\u578B\u670D\u52A1\uFF1B</li><li>\u4F20\u7EDF\u7684\u8FD0\u7EF4\u6A21\u5F0F\u5347\u7EA7\u6210\u81EA\u52A8\u5316\u8FD0\u7EF4\u6A21\u5F0F\uFF0C\u5373\u5168\u65B9\u9762\u7684\u8FD0\u7EF4\u76D1\u63A7\u4F53\u7CFB\uFF0C\u53EF\u81EA\u52A8\u5316\u53EF\u9884\u8B66\uFF1B</li></ul><p>\u6309\u524D\u671F-\u4E2D\u671F-\u540E\u671F\u4E09\u4E2A\u65F6\u95F4\u6BB5\u65F6\u95F4\u6BB5\u3002</p><h2 id="\u5347\u7EA7\u65B9\u6848\u67B6\u6784" tabindex="-1"><a class="header-anchor" href="#\u5347\u7EA7\u65B9\u6848\u67B6\u6784" aria-hidden="true">#</a> \u5347\u7EA7\u65B9\u6848\u67B6\u6784</h2><p>\u7814\u53D1\u6A21\u5F0F\u5347\u7EA7\u6539\u9020\u548C\u4E1A\u52A1\u4E2D\u53F0\u67B6\u6784\uFF0C\u5347\u7EA7\u540E\u65B9\u6848\u5982\u4E0B\uFF1A</p><img src="'+r+'" width="100%"><h2 id="\u5347\u7EA7\u8FC7\u7A0B" tabindex="-1"><a class="header-anchor" href="#\u5347\u7EA7\u8FC7\u7A0B" aria-hidden="true">#</a> \u5347\u7EA7\u8FC7\u7A0B</h2><p>\u4E2D\u53F0\u578B\u7684\u8F6C\u53D8\u9700\u8981\u6574\u4F53\u56E2\u961F\u7684\u4E92\u76F8\u914D\u5408\u3002\u6BCF\u4E2A\u8282\u70B9\u505A\u597D\u81EA\u5DF1\u7684\u4E8B\u60C5\uFF0C\u9700\u8981\u4E0A\u4E0B\u4E00\u5FC3\u7684\u8F6C\u53D8\uFF0C\u9886\u5BFC\u5C42\u7684\u610F\u8BC6\u548C\u53EF\u884C\u6027\u7684\u67B6\u6784\u80FD\u529B\u7684\u652F\u6491\u3002</p><h3 id="\u524D\u671F" tabindex="-1"><a class="header-anchor" href="#\u524D\u671F" aria-hidden="true">#</a> \u524D\u671F</h3><p>\u524D\u671F\u6700\u4E3B\u8981\u7684\u8FD8\u662F\u56E2\u961F\u7684\u610F\u8BC6\uFF0C\u8FD9\u91CC\u7684\u56E2\u961F\u5305\u62EC\u7684\u4E0D\u4EC5\u4EC5\u662F\u4E00\u4E2A\u7EC4\uFF0C\u800C\u662F\u6574\u4E2A\u56E2\u961F\u7684\u4E0A\u4E0B\u610F\u8BC6\uFF0C\u8FD9\u4E2A\u8FC7\u7A0B\u8C03\u6574\u4E86\u7EC4\u7EC7\u7ED3\u6784\uFF0C\u8FD9\u57FA\u672C\u4E0A\u5C06\u539F\u6709\u7684\u9879\u76EE\u7BA1\u7406\u6A21\u5F0F\u5F52\u7531\u7814\u53D1\u90E8\u8FDB\u884C\u6574\u4F53\u6307\u5BFC\uFF0C\u4E3B\u8981\u662F\uFF1A</p><ul><li>\u8C03\u6574\u7EC4\u7EC7\u7ED3\u6784\uFF0C\u6280\u672F\u652F\u6491\u4E0B\u6C89\u5230\u7814\u53D1\u90E8;</li><li>\u6280\u672F\u67B6\u6784\u91CD\u6784\uFF0C\u8FDB\u884C\u4E8C\u6B21\u67B6\u6784\u7684\u5347\u7EA7\uFF0C\u5F62\u6210\u5168\u4F53\u5347\u7EA7\u8F6C\u53D8\u7684\u6307\u5BFC\u601D\u60F3\uFF1B</li><li>\u5C06\u5347\u7EA7\u6218\u7565\u4E0A\u5347\u5230\u56E2\u961F\u6700\u9AD8\u5C42\u9762\uFF0C\u601D\u60F3\u610F\u8BC6\u7EDF\u4E00\uFF1B</li></ul><p>\u4EE5\u4E0A\u4E24\u6B65\u57FA\u672C\u4E0A\u89E3\u51B3\u4E86\u6267\u884C\u7684\u57FA\u7840\u4FDD\u969C\uFF0C\u4E5F\u662F\u4E2D\u53F0\u67B6\u6784\u7684\u57FA\u7840\u4FDD\u969C\u6761\u4EF6\u3002</p><p>\u67B6\u6784\u5347\u7EA7\u7684\u524D\u671F\u9047\u5230\u7684\u6700\u7A81\u51FA\u7684\u95EE\u9898\uFF0C\u65E7\u7CFB\u7EDF\u7684\u5207\u6362\uFF0C\u8FD9\u662F\u4E00\u4E2A\u77DB\u76FE\u70B9\uFF0C\u5728\u4E0D\u8FDD\u80CC\u56E2\u961F\u8FC7\u5927\u7684\u610F\u613F\u4E0B\uFF0C\u8C03\u6574\u7684\u67B6\u6784\u8BBE\u8BA1\uFF0C\u5F53\u4E2D\u4E5F\u505A\u4E86\u5F88\u591A\u59A5\u534F\uFF0C\u53E6\u4E00\u4E2A\u662F\u727A\u7272\u4E00\u90E8\u5206\u6280\u672F\u59A5\u534F\u56E2\u961F\u7684\u63A5\u53D7\uFF0C\u4E00\u6B65\u6B65\u63A8\u8FDB\u3002\u4ECE\u4E0A\u800C\u4E0B\u7684\u4E1A\u52A1\u6A21\u5F0F\u6539\u53D8\uFF0C\u4E3B\u8981\u5305\u62EC\u51E0\u4E2A\u70B9\uFF1A</p><ul><li>\u865A\u62DF\u673A\u8F6C\u53D8\u6210k8s\u5BB9\u5668\u5316\uFF0C\u5347\u7EA7\u865A\u62DF\u673A\u914D\u7F6E\uFF0C\u51CF\u5C11\u670D\u52A1\u5668\u6570\u91CF\uFF1B</li><li>\u53D1\u5E03\u8FC7\u7A0B\u96C6\u6210docker\u5BB9\u5668\u5316\uFF0C\u540C\u65F6\u8C03\u6574\u6700\u5C0F\u914D\u7F6E\uFF0C\u51CF\u5C11\u5927\u5BB6\u7684\u5B66\u4E60\u6210\u672C\uFF1B</li><li>\u6D41\u91CF\u7684\u5207\u5165\u8F6C\u6362\u6210\u5BF9\u5916nodeport\u7684\u5F62\u5F0F\uFF08rpc\u534F\u8BAE\uFF09\uFF1B</li><li>\u670D\u52A1\u5316\u8BBE\u8BA1\u4FDD\u7559\u539F\u6765\u7684\u57FA\u7840\u5DE5\u7A0B\u7ED3\u6784\uFF0C\u4E1A\u52A1\u96C6\u6210CICD\u8FD8\u6709\u670D\u52A1\u8C03\u7528\u6A21\u5F0F\u3002</li></ul><p>\u4E0A\u9762\u51E0\u6B65\u57FA\u672C\u4E0A\u89E3\u51B3\u4E86\u57FA\u7840\u5C42\u7684\u95EE\u9898\u70B9\uFF0C\u8FD8\u6709\u8FC7\u7A0B\u6301\u7EED\u6784\u5EFA\u7684\u5F0A\u7AEF\u70B9\uFF0C\u8D44\u6E90\u4E0D\u7A33\u5B9A\u70B9\u7B49\u3002</p><h3 id="\u4E2D\u524D\u671F" tabindex="-1"><a class="header-anchor" href="#\u4E2D\u524D\u671F" aria-hidden="true">#</a> \u4E2D\u524D\u671F</h3><p>\u57FA\u7840\u5C42\u7684\u89E3\u51B3\u548C\u4FDD\u969C\uFF0C\u6700\u7EC8\u8FD8\u662F\u56DE\u5230\u4E1A\u52A1\u5EFA\u8BBE\u4E0A\u3002</p><p>\u6700\u53EF\u80FD\u51FA\u73B0\u95EE\u9898\u7684\u4E00\u70B9\u548C\u6C89\u6DC0\u80FD\u529B\u7684\u62BD\u53D6\u8003\u8651\u4E0A\uFF0C\u539F\u4E1A\u52A1\u67B6\u6784\uFF0C\u6BD4\u5982\u539F\u6765\u5C31\u6CA1\u6709\u8003\u8651\u5230\u8FD9\u6837\u7684\u65B9\u5F0F\uFF0C\u8FDB\u884C\u7684\u9879\u76EE\u8FD8\u6709\u5DE5\u7A0B\u7814\u53D1\u7B49\uFF0C\u8FD9\u4E2A\u57FA\u672C\u4E0A\u6700\u6709\u53EF\u80FD\u5BFC\u81F4\u540E\u671F\u4E0A\u7EBF\u4E0D\u7A33\u5B9A\u7684\u56E0\u7D20\uFF0C\u505A\u4E86\u51E0\u70B9\uFF1A</p><ul><li>\u57F9\u517B\u548C\u5B8C\u5584\u624B\u518C\uFF0C\u8FD8\u6709\u57F9\u8BAD\u673A\u5236\uFF0C\u5C06\u5404\u4E2A\u8D23\u4EFB\u70B9\u4E0B\u653E\uFF1B</li><li>\u4FDD\u7559\u539F\u670D\u52A1\u5DE5\u7A0B\u7684\u8C03\u7528\u673A\u5236\uFF0C\u5C3D\u91CF\u5728k8s\u4E0A\u8FDB\u884C\u59A5\u534F\u548C\u89E3\u51B3\u95EE\u9898\uFF0C\u5B9E\u5728\u4E0D\u884C\u7684\u518D\u8C03\u6574\u4E1A\u52A1\u4EE3\u7801\uFF1B</li><li>\u5B8C\u5584\u8FD0\u7EF4\u673A\u5236\u548C\u76D1\u63A7\u673A\u5236\uFF0C\u539F\u6709\u7684\u4E1A\u52A1\u6392\u67E5\u65B9\u5F0F\u548C\u53D1\u5E03\u5F0F\u7684\u8F6C\u53D8\uFF1B</li><li>\u591A\u5C42\u9762\u4E0A\u8FDB\u884C\u8FD0\u7EF4\u89D2\u5EA6\u7684\u6574\u5408\uFF0C\u5305\u62EC\u7CFB\u7EDF\u3001\u65E5\u5FD7\u3001\u94FE\u8DEF\u3001\u6D41\u91CF\u3001\u7070\u5EA6\u7B49\u591A\u4E2A\u5C42\u9762\u7684\u4FDD\u969C\uFF1B</li><li>\u53BB\u6389\u4E00\u4E9B\u4E0D\u53EF\u63A7\u7684\u4E1A\u52A1\u8282\u70B9\uFF0C\u4FDD\u7559\u539F\u8FD0\u884C\u6A21\u5F0F\uFF0C\u89C4\u907F\u5404\u7C7B\u5B89\u5168\u7B56\u7565\u7684\u95EE\u9898\uFF0C\u8FD9\u4E5F\u662F\u4E00\u4E2A\u59A5\u534F\u70B9\u3002</li><li>\u4E1A\u52A1\u670D\u52A1\u62BD\u53D6\u53EF\u5F62\u6210\u4EA7\u54C1\u6A21\u5757\uFF0C\u6B64\u4ECE\u4E1A\u52A1\u5C42\u9762\u8FDB\u884C\u59A5\u534F\uFF0C\u8C03\u6574\u4EE3\u7801\u548C\u9879\u76EE\u7ED3\u6784\uFF1B</li><li>\u591A\u5904\u8FD0\u7528\u6D88\u606F\u673A\u5236\uFF0C\u8FDB\u884C\u4E1A\u52A1\u6A21\u5757\u7684\u89E3\u8026\u548C\u53EF\u4EA7\u54C1\u5316\u8C03\u6574\uFF0C\u6B64\u4E5F\u4ECE\u4E1A\u52A1\u5C42\u9762\u4E0A\u8FDB\u884C\u59A5\u534F\u3002</li></ul><p>\u57FA\u4E8E\u4EE5\u4E0A\u591A\u79CD\u7ED3\u6784\u7684\u8C03\u6574\uFF0C\u5B8C\u6210\u4E1A\u52A1\u4E2D\u53F0\u67B6\u6784\u7684\u96CF\u5F62,\u5728\u6709\u9879\u76EE\u7684\u63A8\u8FDB\u3002</p><h3 id="\u4E2D\u671F" tabindex="-1"><a class="header-anchor" href="#\u4E2D\u671F" aria-hidden="true">#</a> \u4E2D\u671F</h3><blockquote><p>\u6B64\u9636\u6BB5\u6709\u53EF\u80FD\u4EA7\u751F\u98CE\u66B4\u671F\uFF0C\u9879\u76EE\u603B\u76D1\u548C\u9879\u76EE\u7ECF\u7406\u9700\u8981\u505A\u597D\u76F8\u5173\u51C6\u5907</p></blockquote><p>\u8FD9\u662F\u4E00\u4E2A\u95EE\u9898\u66B4\u9732\u9636\u6BB5\uFF0C\u8FD9\u4E2A\u8FC7\u7A0B\u9700\u8981\u7684\u5168\u5458\u7684\u914D\u5408\uFF0C\u8FD9\u4E5F\u662F\u4E3A\u4EC0\u4E48\u5728\u524D\u671F\u4E0B\u653E\u8D23\u4EFB\u548C\u601D\u60F3 \u7EDF\u4E00\u7684\u539F\u56E0\u70B9\uFF0C\u4E00\u4E2A\u67B6\u6784\u5E08\uFF0C\u4E00\u4E2A\u90E8\u95E8\u662F\u65E0\u6CD5\u652F\u6491\u8D77\u56E2\u961F\u80FD\u529B\u7684\u3002</p><p>\u521D\u671F\u7684\u9A8C\u8BC1\u548C\u8F6C\u6362\u8C03\u6574\u64CD\u4F5C\uFF0C\u57FA\u672C\u4E0A\u89C9\u5F97\u6CA1\u4EC0\u4E48\u95EE\u9898\uFF0C\u4F46\u73B0\u5B9E\u662F\u4E0D\u53EF\u9884\u77E5\u7684\uFF0C \u4E00\u4E2A\u573A\u666F\u662F\u5728\u524D\u671F\u57FA\u672C\u4E0A\u6309k8s\u670D\u52A1\u4E4B\u95F4\u8C03\u7528\u65B9\u5F0F\u6765\u8FDB\u884C\u7684\uFF0C\u4F46\u662F\u5728\u5185\u5916\u7F51\u7EDC\u9694\u79BB\uFF0C\u8FD8\u6709\u5916\u56F4\u670D\u52A1\u7684\u65F6\u5019\uFF0CIP\u5224\u65AD\u7684\u95EE\u9898\uFF0C\u8FD8\u6709\u5404\u79CD\u7B2C\u4E09\u65B9\u63D2\u4EF6\u7684\u95EE\u9898\u7B49\uFF0C\u8FD8\u6709\u5404\u4E2A\u4F7F\u7528\u7FA4\u4F53\u4E0A\u76F8\u5E94\u8868\u73B0\u60C5\u51B5\u3002</p><p>\u7C7B\u4F3C\u4E8E\u4E0A\u9762\u7684\u573A\u666F\uFF0C\u5E76\u4E0D\u662F\u8BF4\u6709\u591A\u5927\u56F0\u96BE\uFF0C\u8FD9\u4E2A\u8FC7\u7A0B\u662F\u56E2\u961F\u4F1A\u9762\u5BF9\u7684\u95EE\u9898\uFF0C\u5728\u6700\u7EC8\u89E3\u51B3\u4E0B\uFF0C\u4F1A\u66F4\u8FDB\u4E00\u6B65\u7684\u63D0\u5347\u56E2\u961F\u7684\u80FD\u529B\u70B9\uFF0C\u6700\u7EC8\u8FBE\u5230\u5185\u90E8\u63A5\u53D7\u5230\u81EA\u7136\u72B6\u6001\u3002</p><p>\u53E6\u4E00\u4E2A\u662F\u4E00\u4E9B\u64CD\u4F5C\u7684\u4E0D\u6210\u719F\uFF0C\u8FD8\u6709\u9A8C\u8BC1\u6027\uFF0C\u9700\u8981\u5927\u91CF\u7684\u751F\u4EA7\u548C\u6D4B\u8BD5\u9A8C\u8BC1\uFF0C\u4EE5\u786E\u4FDD\u53EF\u884C\u6027\u548C\u540E\u671F\u7684\u7A33\u5B9A\u6027\uFF0C\u8FD9\u4E9B\u524D\u671F\u9A8C\u8BC1\u73AF\u5883\u6709\u64CD\u4F5C\uFF0C\u4F46\u662F\u4E5F\u6709\u5F88\u591A\u672A\u77E5\uFF0C\u8FD9\u4E9B\u90FD\u662F\u8981\u9762\u5BF9\u514B\u670D\u7684\u95EE\u9898\uFF0C \u540C\u6837\u9700\u8981\u6574\u4F53\u56E2\u961F\u7684\u89E3\u51B3\u80FD\u529B\u3002</p><h3 id="\u540E\u671F" tabindex="-1"><a class="header-anchor" href="#\u540E\u671F" aria-hidden="true">#</a> \u540E\u671F</h3><p>\u8FD9\u91CC\u9488\u5BF9\u7684\u662F\u5728\u67B6\u6784\u8BBE\u8BA1\u5C42\u652F\u6491\u548C\u4E2D\u53F0\u7814\u53D1\u56E2\u961F\u67B6\u6784\u7684\u652F\u6491\u4E0A\u7684\u540E\u671F\u3002</p><p>\u5230\u8FD9\u4E00\u6B65\u7684\u65F6\u5019\uFF0C\u4E1A\u52A1\u4E2D\u53F0\u67B6\u6784\u57FA\u672C\u4E0A\u4FBF\u5411\u4E8E\u7A33\u5B9A\uFF0C\u7531\u539F\u6765\u7684\u4E2D\u53F0\u67B6\u6784\u96CF\u5F62\uFF0C\u8F6C\u53D8\u6210\u4E1A\u52A1\u4E2D\u53F0\u67B6\u6784\u3002\u4E0D\u7BA1\u662F\u6280\u672F\u3001\u56E2\u961F\u3001\u4E1A\u52A1\u3001\u4E13\u5BB6\u8FD8\u6709\u5404\u4E2A\u573A\u666F\u4E0B\u7684\u6C89\u6DC0\u90FD\u5DF2\u7ECF\u6709\u4E86\u6C89\u6DC0\u80FD\u529B\uFF0C\u5305\u62EC\u89E3\u51B3\u65B9\u6848\u7B49\uFF0C\u5F62\u6210\u4E00\u5957\u4E1A\u52A1\u4E2D\u53F0\u80FD\u529B\u70B9\u3002</p><img src="'+h+'" width="70%"><p>\u4E3B\u8981\u5305\u62EC\u4EE5\u4E0B\u51E0\u4E2A\u70B9\uFF1A</p><ul><li>\u56E2\u961F\u7EC4\u7EC7\u80FD\u529B\uFF0C\u5F62\u6210\u4E2D\u53F0\u7EC4\u7EC7\u67B6\u6784\u4FDD\u969C\uFF0C\u7814\u53D1\u5C42\u548C\u4E1A\u52A1\u5C42\uFF1B</li><li>\u6280\u672F\u6C89\u6DC0\u80FD\u529B\uFF0C\u5F62\u6210\u6280\u672F\u4E2D\u53F0\u548C\u7814\u53D1\u652F\u6491\u5C42\uFF1B</li><li>\u4EA7\u54C1\u8F93\u51FA\u80FD\u529B\uFF0C\u5F62\u6210\u884C\u4E1A\u4EA7\u54C1\u6C89\u6DC0\uFF0C\u5F62\u6210\u57FA\u7840\u7684\u884C\u4E1A\u6807\u51C6\u4EA7\u54C1\u7EC4\u4EF6\uFF1B</li><li>\u89E3\u51B3\u65B9\u6848\u80FD\u529B\uFF0C\u5728\u884C\u4E1A\u8F6F\u4EF6\u4E2D\u5F62\u6210\u4E00\u5957\u53EF\u884C\u7684\u89E3\u51B3\u65B9\u6848\uFF1B</li><li>\u4E2D\u53F0\u4E1A\u52A1\u80FD\u529B\uFF0C\u4E1A\u52A1\u5927\u4E2D\u53F0\u4EA7\u51FA\uFF0C\u5C0F\u524D\u53F0\u4E1A\u52A1\u5EFA\u8BBE\u7684\u7EFC\u5408\u80FD\u529B\u3002</li></ul><p>\u4F59\u4E0B\u7684\uFF0C\u66F4\u591A\u7684\u662F\u67B6\u6784\u8BBE\u8BA1\u7684\u524D\u671F\u95EE\u9898\u68B3\u7406\u548C\u8FDB\u4E00\u6B65\u7684\u4F18\u5316\uFF0C\u5404\u4E2A\u7BA1\u7406\u529F\u80FD\u7684\u5B8C\u5584\u548C\u8FDB\u4E00\u6B65\u6C89\u6DC0\u4EA7\u54C1\u80FD\u529B\uFF0C\u96C6\u6210\u5230\u4E2D\u53F0\u7BA1\u7406\u5E73\u53F0\u4E0A\uFF0C\u540C\u65F6\u5728\u5404\u4E2A\u56E2\u961F\u548C\u5C0F\u7EC4\u4E4B\u95F4\u8FDB\u884C\u7ECF\u9A8C\u5206\u4EAB\u548C\u603B\u7ED3\u3002\u540C\u65F6\u4E3A\u4E0B\u4E00\u4E2A\u4E1A\u52A1\u573A\u666F\u8FDB\u884C\u68B3\u7406\u3002</p><h2 id="\u5176\u5B83" tabindex="-1"><a class="header-anchor" href="#\u5176\u5B83" aria-hidden="true">#</a> \u5176\u5B83</h2><p>\u5728\u4E1A\u52A1\u4E2D\u53F0\u5316\u5EFA\u8BBE\u7684\u540E\u671F\uFF0C\u5404\u4E2A\u4E1A\u52A1\u504F\u5411\u4E8E\u7A33\u5B9A\uFF0C\u5728\u4E2D\u53F0\u57FA\u7840\u4E0A\u878D\u5165\u4EA7\u54C1\u80FD\u529B\u8303\u56F4\u3002</p>',39),t=[p];function n(o,c){return a(),e("div",null,t)}var _=i(d,[["render",n],["__file","09_\u4F20\u7EDF\u4E1A\u52A1\u5347\u7EA7\u4E2D\u53F0\u89E3\u51B3\u65B9\u6848.html.vue"]]);export{_ as default};
