import{_ as r,o as a,c as e,d as t}from"./app.952e3db8.js";var n="/sso/sso_03.png";const s={},o=t('<h1 id="\u7EDF\u4E00\u8EAB\u4EFD\u8BA4\u8BC1\u89E3\u51B3\u65B9\u6848" tabindex="-1"><a class="header-anchor" href="#\u7EDF\u4E00\u8EAB\u4EFD\u8BA4\u8BC1\u89E3\u51B3\u65B9\u6848" aria-hidden="true">#</a> \u7EDF\u4E00\u8EAB\u4EFD\u8BA4\u8BC1\u89E3\u51B3\u65B9\u6848</h1><h2 id="\u6982\u8FF0" tabindex="-1"><a class="header-anchor" href="#\u6982\u8FF0" aria-hidden="true">#</a> \u6982\u8FF0</h2><p>\u8EAB\u4EFD\u8BA4\u8BC1\uFF0C\u5C31\u662F\u5224\u65AD\u4E00\u4E2A\u7528\u6237\u662F\u5426\u4E3A\u5408\u6CD5\u7528\u6237\u7684\u5904\u7406\u8FC7\u7A0B\uFF0C\u7EDF\u4E00\u8EAB\u4EFD\u8BA4\u8BC1\u5373\u662F\u5BF9\u5404\u4E2A\u7CFB\u7EDF\u7684\u7528\u6237\u8FDB\u884C\u7EDF\u4E00\u5904\u7406\uFF0C\u901A\u8FC7\u7EDF\u4E00\u8EAB\u4EFD\u8BA4\u8BC1\uFF0C\u53EF\u4EE5\u5BF9\u6240\u6709\u7CFB\u7EDF\u8FDB\u884C\u7EDF\u4E00\u8EAB\u4EFD\u7BA1\u7406\uFF0C\u7EDF\u4E00\u6743\u9650\u7BA1\u7406</p><h2 id="\u65B9\u6848\u67B6\u6784" tabindex="-1"><a class="header-anchor" href="#\u65B9\u6848\u67B6\u6784" aria-hidden="true">#</a> \u65B9\u6848\u67B6\u6784</h2><p>\u5355\u70B9\u767B\u9646\u89E3\u51B3\u65B9\u6848\u67B6\u6784\uFF0C\u96C6\u6210\u5E94\u7528\u7BA1\u7406\u95E8\u6237\uFF0C\u96C6\u6210\u591A\u4E2A\u4E1A\u52A1\u7CFB\u7EDF\u7BA1\u7406\uFF0C\u67B6\u6784\u5982\u4E0B\uFF1A</p><img src="'+n+'" width="70%"><p>\u6574\u4F53\u67B6\u6784\u63CF\u8FF0\uFF1A</p><ul><li>\u57FA\u4E8E\u4E2D\u53F0\u95E8\u6237\u7BA1\u7406\u591A\u5E94\u7528\uFF0C\u5305\u62EC\u5E94\u7528\u7684\u6DFB\u52A0\u3001\u4FEE\u6539\u3001\u7981\u7528\u7B49</li><li>\u9488\u5BF9\u4E8E\u5355\u70B9\u767B\u9646\u8BA4\u8BC1\u7684\u7BA1\u7406\uFF0C\u5305\u62EC\u7528\u6237\u7BA1\u7406\uFF0C\u4E2A\u6027\u5316\u914D\u7F6E</li></ul><h2 id="\u7528\u6237\u573A\u666F" tabindex="-1"><a class="header-anchor" href="#\u7528\u6237\u573A\u666F" aria-hidden="true">#</a> \u7528\u6237\u573A\u666F</h2><p>\u516C\u53F8\u4E2D\u5B58\u5728\u591A\u4E2A\u7CFB\u7EDF\uFF0C\u4F46\u662F\u5404\u4E2A\u7CFB\u7EDF\u90FD\u81EA\u5DF1\u7EF4\u62A4\u4E86\u4E00\u5957\u8EAB\u4EFD\u8BA4\u8BC1\u7CFB\u7EDF\uFF0C\u8FD9\u6837\u5B50\u5728\u4E0D\u540C\u7CFB\u7EDF\u8981\u8BB0\u5F55\u4E0D\u540C\u8D26\u53F7\u3001\u53E3\u4EE4\u7B49\u4FE1\u606F\uFF0C\u5341\u5206\u9EBB\u70E6\uFF0C\u7279\u522B\u662F\u5BF9\u4E8E\u6709\u5173\u8054\u7684\u4E1A\u52A1\u7CFB\u7EDF\u800C\u8A00\u66F4\u662F\u5982\u6B64\uFF0C\u5C31\u6BD4\u5982\u4E00\u4E2A\u5BA2\u6237\uFF0C\u4ED6\u8981\u8FDB\u884C\u4E00\u6B21\u8D2D\u7269\u65F6\uFF0C\u8981\u6CE8\u518C\u82E5\u5E72\u4E2A\u8D26\u53F7\uFF0C\u81EA\u7136\u4F1A\u89C9\u5F97\u9EBB\u70E6\u3002</p><p><strong>\u7EDF\u4E00\u8EAB\u4EFD\u7BA1\u7406\u7684\u9700\u8981</strong> \u6CA1\u6709\u7EDF\u4E00\u8EAB\u4EFD\u7BA1\u7406\u7CFB\u7EDF\uFF0C\u9488\u5BF9\u4E00\u4E2A\u7528\u6237\uFF0C\u6BCF\u4E2A\u7CFB\u7EDF\u81F3\u5C11\u5C31\u90FD\u8981\u7EF4\u62A4\u4E00\u4E2A\u76F8\u5BF9\u5E94\u7684\u8D26\u53F7\uFF0C\u8FD9\u610F\u5473\u7740\u7528\u6237\u4FEE\u6539\u8D26\u53F7\u4FE1\u606F\u65F6\u5C31\u4E0D\u5F97\u4E0D\u5230\u5404\u4E2A\u7CFB\u7EDF\u4E2D\u53BB\u4FEE\u6539\uFF0C\u5341\u5206\u9EBB\u70E6\uFF0C\u800C\u4E14\u8D26\u53F7\u7684\u5206\u522B\u7BA1\u7406\u4E5F\u4E0D\u597D\u7EF4\u62A4</p><p><strong>\u7EDF\u4E00\u6743\u9650\u7BA1\u7406\u7684\u9700\u8981</strong> \u5982\u679C\u6709\u7EDF\u4E00\u8EAB\u4EFD\u7BA1\u7406\u7CFB\u7EDF\uFF0C\u4F46\u662F\u6CA1\u6709\u76F8\u5E94\u7684\u6743\u9650\u7BA1\u7406\u7CFB\u7EDF\uFF0C\u90A3\u5C31\u610F\u5473\u8FD9\u4E00\u4E2A\u8D26\u53F7\u53EF\u4EE5\u770B\u5230\u4E0D\u5C5E\u4E8E\u8FD9\u4E2A\u8D26\u53F7\u7684\u5185\u5BB9\uFF0C\u8FD9\u80AF\u5B9A\u662F\u4E0D\u884C\u7684\uFF0C\u6240\u4EE5\u8EAB\u4EFD\u7BA1\u7406\u548C\u6743\u9650\u7BA1\u7406\u90FD\u662F\u914D\u5957\u7684</p><p><strong>\u7EDF\u4E00\u6388\u6743\u7BA1\u7406\u7684\u9700\u8981</strong> \u5982\u6709\u7EDF\u4E00\u8EAB\u4EFD\u7BA1\u7406\u548C\u7EDF\u4E00\u6743\u9650\u7BA1\u7406\uFF0C\u4F46\u662F\u6CA1\u6709\u5355\u70B9\u767B\u5F55\u548C\u7CFB\u7EDF\u6388\u6743\u65F6\uFF0C\u7528\u6237\u767B\u5F55\u7CFB\u7EDF\u65F6\u8FD8\u662F\u8981\u6CA1\u8981\u8FDB\u4E00\u4E2A\u7CFB\u7EDF\u90FD\u53BB\u767B\u5F55\u4E00\u6B21\uFF0C\u7279\u522B\u9EBB\u70E6\uFF0C\u4F46\u7CFB\u7EDF\u4E00\u591A\uFF0C\u66F4\u662F\u5982\u6B64</p><h2 id="\u75DB\u70B9" tabindex="-1"><a class="header-anchor" href="#\u75DB\u70B9" aria-hidden="true">#</a> \u75DB\u70B9</h2><p><strong>\u7EC4\u7EC7\u673A\u6784</strong> \u5BF9\u4E8E\u4E00\u4E2A\u4F53\u7CFB\u5E9E\u5927\u7684\u673A\u6784\uFF0C\u7EC4\u7EC7\u673A\u6784\u7BA1\u7406\u662F\u5FC5\u987B\u7684\uFF0C\u9700\u8981\u652F\u6301\u591A\u7EF4\u5EA6\u7EC4\u7EC7\u673A\u6784\u7BA1\u7406\uFF0C\u4EE5\u53CA\u57FA\u4E8E\u591A\u673A\u6784\u7684\u517C\u804C\u3001\u8C03\u52A8\u3001\u501F\u8C03\u3001\u5408\u5E76\uFF0C\u5E76\u4E14\u9700\u8981\u80FD\u5206\u7EA7\u6388\u6743\uFF0C\u652F\u6301\u6309\u7167\u90E8\u95E8\u7684\u5206\u7EA7\u6388\u6743\u7BA1\u7406\uFF0C\u7BA1\u7406\u5458\u53EF\u4EE5\u5C06\u81EA\u5DF1\u53EF\u7BA1\u7406\u7684\u8EAB\u4EFD\u5B50\u96C6\u7BA1\u7406\u6743\u9650\u518D\u5206\u914D\uFF0C\u5B9E\u73B0\u7EDF\u4E00\u5B58\u50A8\uFF0C\u5206\u7EA7\u7BA1\u7406</p><p><strong>\u79DF\u7528\u7BA1\u7406</strong> \u4E00\u4E9B\u8D26\u53F7\u5982\u679C\u540C\u5C5E\u4E8E\u4E00\u4E2A\u56E2\u4F53\uFF0C\u8FD9\u4E2A\u56E2\u4F53\u79DF\u7528\u4E86\u7CFB\u7EDF\uFF0C\u90A3\u4E48\u8FD9\u4E2A\u5F53\u8FD9\u4E2A\u56E2\u4F53\u79DF\u7528\u5230\u671F\u65F6\uFF0C\u5C31\u8981\u53BB\u7981\u7528\u8FD9\u4E2A\u56E2\u4F53\u7684\u6240\u6709\u8D26\u53F7\uFF0C\u4E00\u4E2A\u4E00\u4E2A\u53BB\u5F04\u592A\u8FC7\u9EBB\u70E6\uFF0C\u7279\u522B\u5F53\u56E2\u4F53\u8D26\u53F7\u5F88\u591A\u65F6\u66F4\u662F\u5982\u6B64</p><p><strong>\u8D26\u53F7\u6388\u6743</strong> \u4E00\u4E2A\u8D26\u53F7\u80FD\u8BBF\u95EE\u7684\u5E94\u7528\u6709\u4E2A\u8303\u56F4\uFF0C\u800C\u5728\u5E94\u7528\u4E2D\u6240\u80FD\u8BBF\u95EE\u7684\u6A21\u5757\u4E5F\u6709\u4E2A\u8303\u56F4\uFF0C\u7684\u56E0\u6B64\u9700\u8981\u80FD\u7ED9\u8D26\u53F7\u8FDB\u884C\u5E94\u7528\u6388\u6743\uFF0C\u5E76\u4E14\u8FDB\u884C\u6A21\u5757\u6388\u6743\uFF0C\u66F4\u8FDB\u4E00\u5C42\u8FD8\u8981\u6709\u6309\u94AE\u7EA7\u522B\u6388\u6743</p><p><strong>\u81EA\u670D\u52A1\u7BA1\u7406</strong> \u7528\u6237\u8981\u901A\u8FC7\u81EA\u670D\u52A1\u9875\u9762\u4FEE\u6539(\u6388\u6743\u5141\u8BB8\u5176\u4FEE\u6539\u7684)\u4E2A\u4EBA\u5C5E\u6027\uFF0C\u7528\u6237\u8981\u80FD\u81EA\u52A9\u4FEE\u6539\u7EDF\u4E00\u7528\u6237\u540D\uFF0C\u81EA\u52A9\u4FEE\u6539\u7EDF\u4E00\u7528\u6237\u5BC6\u7801\uFF0C\u81EA\u52A9\u627E\u56DE\u5BC6\u7801\uFF0C\u4EE5\u53CA\u67E5\u770B\u5176\u6240\u62E5\u6709\u7684\u5E94\u7528\u8BBF\u95EE\u6743\u9650\u548C\u5E94\u7528\u8D26\u53F7\u5217\u8868\u3002</p><p><strong>\u591A\u56E0\u7D20\u8BA4\u8BC1</strong> \u5E94\u5177\u5907\u591A\u79CD\u8BA4\u8BC1\u65B9\u5F0F\uFF08\u5305\u542B\uFF1A\u5BC6\u7801\u3001OPT\u3001\u6307\u7EB9\u3001\u4E8C\u7EF4\u7801\u3001\u77ED\u4FE1\u7B49\uFF09\uFF0C\u8FD9\u6837\u5E94\u7528\u80FD\u591F\u6839\u636E\u5404\u81EA\u7684\u5B89\u5168\u5F3A\u5EA6\u8981\u6C42\u9009\u62E9\u76F8\u5E94\u7684\u8BA4\u8BC1\u65B9\u5F0F</p><h2 id="\u65B9\u6848\u4F18\u52BF" tabindex="-1"><a class="header-anchor" href="#\u65B9\u6848\u4F18\u52BF" aria-hidden="true">#</a> \u65B9\u6848\u4F18\u52BF</h2><p><strong>\u96C6\u6210\u7684\u79DF\u6237\u7BA1\u7406</strong> \u53EF\u4EE5\u8FDB\u884C\u79DF\u6237\u7BA1\u7406\uFF0C\u901A\u8FC7\u7ED9\u8D26\u53F7\u8BBE\u7F6E\u79DF\u6237\uFF0C\u5F53\u56E2\u4F53\u79DF\u7528\u5230\u671F\u662F\uFF0C\u53EF\u4EE5\u901A\u8FC7\u5BF9\u79DF\u6237\u7BA1\u7406\u7EDF\u4E00\u7BA1\u7406\u56E2\u4F53\u8D26\u6237\uFF0C\u4E0D\u7528\u518D\u4E00\u4E2A\u4E00\u4E2A\u53BB\u8BBE\u7F6E\u8D26\u6237\u4E86</p><p><strong>RBAC \u6A21\u578B</strong> \u901A\u8FC7\u9009\u62E9\u9879\u76EE\uFF0C\u8BBE\u7F6E\u9879\u76EE\u89D2\u8272\u4E3A\u9879\u76EE\u8BBE\u7F6E\u4E0D\u540C\u7684\u6743\u9650\u7EA7\u522B\uFF0C\u7136\u540E\u7ED9\u8D26\u6237\u6388\u4E88\u76F8\u5E94\u89D2\u8272\u5C31\u53EF\u7ED9\u8D26\u6237\u5206\u914D\u76F8\u5E94\u6743\u9650\uFF0C\u8FD8\u80FD\u5728\u9879\u76EE\u8BBE\u7F6E\u90A3\u901A\u8FC7\u6743\u9650\u6807\u8BC6\u8BBE\u7F6E\u6309\u94AE\u7684\u53EF\u89C1\u6027</p><p><strong>\u96C6\u6210\u81EA\u670D\u52A1\u7BA1\u7406</strong> \u7528\u6237\u767B\u5F55\u540E\uFF0C\u53EF\u4EE5\u901A\u8FC7\u7528\u6237\u4E2D\u5FC3\u4FEE\u6539\u81EA\u5DF1\u7684\u7528\u6237\u4FE1\u606F\uFF0C\u8D26\u6237\u7684\u5BC6\u7801\uFF0C\u5F53\u5FD8\u8BB0\u5BC6\u7801\u65F6\u8FD8\u53EF\u4EE5\u901A\u8FC7\u767B\u5F55\u9875\u7684\u5FD8\u8BB0\u5BC6\u7801\u91CD\u8BBE\u5BC6\u7801</p><p><strong>\u96C6\u6210\u7EC4\u7EC7\u673A\u6784\u7BA1\u7406</strong> \u672C\u65B9\u6848\u96C6\u6210\u4E86\u7EC4\u7EC7\u673A\u6784\u7BA1\u7406\uFF0C\u53EF\u4EE5\u4FEE\u6539\u90E8\u95E8\u7ED3\u6784\uFF0C\u5728\u8D26\u53F7\u8BBE\u7F6E\u90A3\u53EF\u4EE5\u8BBE\u7F6E\u8D26\u53F7\u6240\u5C5E\u90E8\u95E8</p><p><strong>\u96C6\u6210\u5E38\u7528\u529F\u80FD</strong> \u96C6\u6210\u4E86\u5B57\u5178\u7BA1\u7406\uFF0C\u53C2\u6570\u914D\u7F6E\u7B49\u7B49\u5E38\u7528\u529F\u80FD\uFF0C\u65B9\u4FBF\u5F00\u53D1\u4EFB\u52A1\u8FDB\u884C\u5F00\u53D1\u6539\u9020</p><p><strong>\u754C\u9762\u7B80\u6D01\u6E05\u695A\uFF0C\u64CD\u4F5C\u7B80\u5355</strong> \u9879\u76EE\u7BA1\u7406\uFF0C\u89D2\u8272\u7BA1\u7406\uFF0C\u8D26\u53F7\u7BA1\u7406\uFF0C\u90E8\u95E8\u7BA1\u7406\u7B49\u7B49\uFF0C\u4E00\u76EE\u4E86\u7136\uFF0C\u64CD\u4F5C\u7B80\u5355\u5BB9\u6613\u4E0A\u624B</p><h2 id="\u63A8\u8350\u4EA7\u54C1" tabindex="-1"><a class="header-anchor" href="#\u63A8\u8350\u4EA7\u54C1" aria-hidden="true">#</a> \u63A8\u8350\u4EA7\u54C1</h2><p>\u65E0</p>',28),h=[o];function d(p,i){return a(),e("div",null,h)}var c=r(s,[["render",d],["__file","22_\u7EDF\u4E00\u8EAB\u4EFD\u8BA4\u8BC1\u89E3\u51B3\u65B9\u6848.html.vue"]]);export{c as default};
